targets.expr:
quantile(
  $percentile / 100, (
    avg (
      sum (
        irate(node_cpu{mode=~"(system|user|irq|nice)", instance=~'[[osd_servers_raw]].*'}[1m]))
      by (instance,cpu)
    ) by (instance)
  ) * 100
)

quantile(
  $percentile/100, 
  (
    node_memory_MemTotal{job='node'} - node_memory_MemFree{job='node'} - node_memory_Buffers{job='node'} - node_memory_Cached{job='node'}
  ) / node_memory_MemTotal{job='node'} * 100
)

sum(
  sum(
    rate(
      node_disk_reads_completed[$__interval]
    )
  ) by (instance,device)
   + ignoring(ceph_daemon,job) group_right(instance) ceph_disk_occupation
)
+
sum(
  sum(
    rate(
      node_disk_writes_completed[$__interval]
    )
  ) by (instance,device)
   + ignoring(ceph_daemon,job) group_right(instance) ceph_disk_occupation
)

# should only include OSD hosts
sum(
  sum(
    rate(
      node_disk_bytes_read[$__interval]
    )
  ) by (instance,device)
  + ignoring(ceph_daemon,job) group_right(instance) ceph_disk_occupation
)
+
sum(
  sum(
    rate(
      node_disk_bytes_written[$__interval]
    )
  ) by (instance,device)
  + ignoring(ceph_daemon,job) group_right(instance) ceph_disk_occupation
)

# will include non-OSD hosts
sum(
  rate(
    node_disk_bytes_read[$__interval]
  ) + 
  rate(
    node_disk_bytes_written[$__interval]
  )
)

avg (
  max by (device) (
    irate(node_disk_io_time_ms[1m]) 
    and on (instance, device) ceph_disk_occupation
  ) / 10
)

quantile(
  $percentile/100, 
  (
    max by (device) (
      irate(node_disk_io_time_ms[1m]) 
      and on (instance, device) ceph_disk_occupation
    ) / 10
  )
)
